module.runOnceOnLoad(function(){

                var appendButton=function(){
                    module.getCloud().getElement().appendChild(new Element('span', {
						html:"+",
						"class":"tag-el add-tag",
						events:{
							click:function(){

							    var newItem=new MockDataTypeItem({name:"New Thing", mutable:true, kind:"layer keyword"});

								(new UIModalDialog(application, newItem, {
		                		"formName":"newCloudValueForm"})).show();

		                		newItem.addEvent('save',function(){
		                		    module.addValue(newItem.getName());
		                		    appendButton();
		                		});

							}
						}
					}));
                };

                appendButton();

});

